- import { objectType } from 'nexus'
+ import { schema } from 'nexus'

- export const User = objectType({
+ schema.objectType({
   name: 'User',
   ...
})

+++ app.ts
+ import { schema } from 'nexus'

+ schema.addToContext(req => {
+   return { ... }
+ })

- // Example
- import { GraphQLServer } from 'graphql-yoga'

- new GraphQLServer({
-    context(req) => {
-      return { ... }
-   }
- })

