    },
  ],
  // ...
})
// ...

import { schema } from 'nexus'

export type A = {
  /* ... */
}

schema.objectType({
  rootTyping: 'A',
  // ...
})

makeSchema({
  nonNullDefaults: {
    input: false,
    output: false,
  },
})

