// api/graphql/Post.ts

import { schema } from 'nexus'

schema.extendType({
  type: 'Mutation',
  definition(t) {
    // ...
    t.field('publish', {
      type: 'Post',
      args: {
        draftId: schema.intArg({ required: true }),
      },
      resolve(_root, args, ctx) {
        let draftToPublish = ctx.inDb.posts.find((p) => p.id === args.draftId)

        if (!draftToPublish) {
          throw new Error('Could not find draft with id ' + args.draftId)
        }

        draftToPublish.published = true

        return draftToPublish
      },
    })
  },
})

type Mutation {
  createDraft(body: String!, title: String!): Post
+ publish(draftId: Int!): Post
}

// api/graphql/Post.ts

import { schema } from 'nexus'

schema.extendType({
  name: 'Query',
  definition(t) {
    // ...
    t.list.field('posts', {
      type: 'Post',
      resolve(_root, _args, ctx) {
        return ctx.inDb.posts.filter((p) => p.published === true)
      },
    })
  },
})

mutation {
  publish(draftId: 1) {
    id
    title
    body
    published
  }
}

query {
  posts {
    id
    title
    body
    published
  }
}

