+ publish(draftId: Int!): Post
}
`}</code></pre></div><div class="IntrinsicRow"><pre parentName="section"><code parentName="pre" {...{
            "className": "language-ts"
          }}>{`// api/graphql/Post.ts

import { schema } from 'nexus'

schema.extendType({
  name: 'Query',
  definition(t) {
    // ...
    t.list.field('posts', {
      type: 'Post',
      resolve(_root, _args, ctx) {
        return ctx.inDb.posts.filter((p) => p.published === true)
      },
    })
  },
})
`}</code></pre><pre parentName="section"><code parentName="pre" {...{
