import { schema } from 'nexus'

schema.objectType({
  name: 'Foo',
  definition(t) {
    t.string('bar', () => 'qux')
  },
})

                                      schema.objectType({
// The singleton instance of ---------^      |
// the Nexus schema component                |
//                                           |
// A type Builder method --------------------^
                                        name: 'Foo',
// The name of this type ----------------------^
                                        definition(t) {
// The type definition block -----------^          |
// Where fields are defined                        |
//                                                 |
// Object of Object Type Field --------------------^
// Builder methods
                                          t.field('bar', {
// A field builder method ------------------^      |
// The name of this field -------------------------^
                                            type: 'Bar',
// The type of this field -------------------------^
                                            resolve(parent, args, ctx, info) {
// The method called to return a -----------^       |       |     |    |
// value for this field when queried                |       |     |    |
//                                                  |       |     |    |
// The backing data model for Foo ------------------^       |     |    |
//                                                          |     |    |
// The client arguments to this field ----------------------^     |    |
//                                                                |    |
// Contextual data for this request ------------------------------^    |
// Shared across all resolvers                                         |
//                                                                     |
// Technical detail about this request --------------------------------^
// E.g. client's query AST

// Your logic to return a value ------------> ...
// for this field
                                            },
                                          })

                                          t.string('qux')
// A scalar-type convenience builder -------^       |
//                                                  |
// No resolver means Nexus returns the -------------^
// `qux` property from the backing data model

                                        },
                                      })

schema.objectType({
  name: 'Alpha',
  definition(t) {
    t.id('a')
    t.string('b')
    t.int('c')
    t.float('d')
    t.boolean('e')
  },
})

schema.objectType({
  name: 'Alpha',
  definition(t) {
    t.field('beta', {
      type: 'Beta',
      resolve() {
        return { foo: 'bar' }
      },
    })
  },
})
schema.objectType({
  name: 'Beta',
  definition(t) {
    t.string('foo')
  },
})

type Alpha {
  beta: Beta!
}

type Beta {
  foo: String!
}

schema.objectType({
  name: 'Alpha',
  definition(t) {
    t.id('a', { nullable: true })
    t.list.id('b')
    t.list.id('c', { nullable: true })
    t.list.id('c', { list: [false] })
    t.list.id('c', { list: [false], nullable: true })
  },
})

schema.queryType({
  definition(t) {
    t.field('anyAlpha', {
      type: 'Alpha',
      resolve(t) {
        return Math.random() > 0.1 : 'Zeta' : 'Yolo'
      }
    })
    t.list.field('alphas', {
      type: 'Alpha',
      args: {
        except: schema.arg({
          list: true,
          type: "Alpha",
          required: true,
        })
      },
      resolve(_root, args) {
        return ['Zeta', 'Yolo'].filter(alpha => {
          return !args.except.includes(alpha)
        })
      }
    })
  }
})

schema.queryType({
  definition(t) {
    t.string('echo', {
      args: {
        message: 'String',
      },
      resolve(_root, args) {
        return args.message ?? 'nil'
      },
    })
  },
})

settings.change({
  schema: {
    nullable: {
      outputs: false,
      inputs: false,
    },
  },
})

schema.queryType({
  definition(t) {
    t.string('echo', {
      args: {
        message: 'String',
      },
      resolve(_root, args) {
        return args.message
      },
    })
  },
})

schema.queryType({
  nonNullDefaults: {
    input: true,
    output: true,
  },
  definition(t) {
    t.string('echo', {
      args: {
        message: 'String',
      },
      resolve(_root, args) {
        return args.message
      },
    })
  },
})

schema.queryType({
  definition(t) {
    t.string('echo', {
      args: {
        message: schema.arg({
          type: 'String',
          nullable: false,
        }),
      },
      nullable: false,
      resolve(_root, args) {
        return args.message
      },
    })
  },
})

schema.queryType({
  // flip the global defaults
  nonNullDefaults: {
    input: true,
    output: true,
  },
  definition(t) {
    // ... Everything in this type uses the type-level
    // nullability config ... Except the following,
    // which effectively reverts back to what the global
    // defaults are:
    t.string('echo', {
      nullable: false,
      args: {
        message: schema.arg({
          type: 'String',
          nullable: false,
        }),
      },
      resolve(_root, args) {
        return args.message
      },
    })
  },
})

schema.queryType({
  definition(t) {
    t.string('echo', {
      args: {
        message: schema.arg({
          type: 'String',
          default: 'nil via default',
        }),
      },
      nullable: false,
      resolve(_root, args) {
        const fallback = 'nil via client null'
        return args.message ?? fallback
      },
    })
  },
})

type Query {
  echo(message: String = "nothing via default"): String!
}}

query {
  echo1: echo
  echo2: echo(message: null)
}

schema.query({
  definition(t) {
    t.user({
      args: {
        id: schema.arg({ type: 'ID', required: true }),
      },
      resolve(_, { id }, { db }) {
        return db.fetchUser({ where: { id } })
      },
    })
  },
})

schema.object({
  name: 'User',
  rootTyping: 'Prisma.User',
  definition(t) {
    t.string('fullName', {
      resolve(user) {
        return [user.firstName, user.middleName, user.lastName].join(', ')
      },
    })
    t.int('age', {
      resolve(user) {
        return yearsSinceUnixTimestamp(user.birthDate)
      },
    })
    t.list.field('comments', {
      type: 'Comment',
      resolve(user, _args, { db }) {
        return db.comment.fetchMany({ where: { author: user.id } })
      },
    })
  },
})

schema.object({
  name: 'Comment',
  rootTyping: 'Prisma.Comment',
  definition(t) {
    t.string('title', {
      resolve(comment) {
        return comment.title
      },
    })
    t.field('body', {
      resolve(comment) {
        return comment.body
      },
    })
    t.field('post', {
      type: 'Post',
      resolve(comment, _args, { db }) {
        return db.post.fetchOne({ where: { id: comment.postId } })
      },
    })
    t.field('author', {
      type: 'User',
      resolve(comment, _args, { db }) {
        return db.user.fetchOne({ where: { id: comment.authorId } })
      },
    })
  },
})

schema.object({
  name: 'Post',
  rootTyping: 'Prisma.Post',
  definition(t) {
    t.string('title', {
      resolve(post) {
        return post.title
      },
    })
    t.field('body', {
      resolve(post) {
        return post.body
      },
    })
    t.list.field('comments', {
      type: 'Comment',
      resolve(post, _args, { db }) {
        return db.comment.fetchMany({ where: { id: post.commentId } })
      },
    })
  },
})

// Nexus infers the backing type of:
//
// { fullName: string, age: number } ---> |
//                                        |
schema.object({                        // |
  name: 'User',                        // |
  definition(t) {                      // |
    t.string('fullName', {             // |
      resolve(user) {                  // |
//            ^-------------------------- | 
        return user.fullName           // |
      },                               // |
    })                                 // |
    t.int('age', {                     // |
      resolve(user) {                  // |
//            ^-------------------------- |
        return user.age                // |
      },                               // |
    })                                 // |
  },                                   // |
})                                     // |
                                       // |
schema.queryType({                     // |
  definition(t) {                      // |
    t.list.field('users', {            // |
      type: 'User',                    // |
      resolve() {                      // |
        return [/**/]                  // |
//               ^----------------------- |
      },
    })
  },
})

export interface MyDBUser {
  // |           ^-------------------- Create your backing type
  // ^-------------------------------- Export your backing type (required)
  firstName: string
  lastName: string
  birthDate: number
}

schema.object({
  name: 'User',
  rootTyping: 'MyDBUser',
  //           ^---------------------- Tell Nexus what the backing type is.
  //                                   Now, Nexus types...
  definition(t) {
    t.string('fullName', {
      resolve(user) {
        //    ^----------------------- as: MyDBUser
        return [user.firstName, user.lastName].join(', ')
      },
    })
    t.int('age', {
      resolve(user) {
        //    ^------------------------ as: MyDBUser
        return yearsSinceUnixTimestamp(user.birthDate)
      },
    })
  },
})

schema.queryType({
  definition(t) {
    t.list.field('users', {
      type: 'User',
      resolve(_root, args, ctx) {
        //                     ^------- return as: MyDBUser[]
        return ctx.db.user.getMany()
      },
    })
  },
})

export type * as Spotify from 'spotify-api'
//                             ^------ Export your third-party type(s)
//                                     Can be anywhere within your app

schema.object({
  name: 'User',
  rootTyping: 'Spotify.Foo',
  //           ^---------------------- Tell Nexus what the backing type is.
  //                                   Now, Nexus types...
  definition(t) {
    t.string('fullName', {
      resolve(user) {
        //    ^----------------------- as: Spotify.Foo
        return user.fullName
      },
    })
    t.int('age', {
      resolve(user) {
        //    ^------------------------ as: Spotify.Foo
        return user.age
      },
    })
  },
})

schema.queryType({
  definition(t) {
    t.list.field('users', {
      type: 'User',
      resolve(_root, args, ctx) {
        //                     ^------- return as: Spotify.Foo[]
        return ctx.db.user.getMany()
      },
    })
  },
})

