schema.object({
  name: 'User',
  rootTyping: 'Spotify.Foo',
  //           ^---------------------- Tell Nexus what the backing type is.
  //                                   Now, Nexus types...
  definition(t) {
    t.string('fullName', {
      resolve(user) {
        //    ^----------------------- as: Spotify.Foo
        return user.fullName
      },
