import { TestContext, createTestContext } from 'nexus/testing'

let ctx: TestContext

beforeAll(async () => {
  Object.assign(ctx, await createTestContext())
  await ctx.app.start()
})

afterAll(async () => {
  await ctx.app.stop()
})

test('hello', async () => {
  const result = await ctx.query(`{ hello }`)

  expect(result).toMatchInlineSnapshot()
})

