+++ prisma/schema.prisma
+
+  datasource db {
+    provider = "sqlite"
+    url      = "file:dev.db"
+  }
+
+  generator prisma_client {
+    provider = "prisma-client-js"
+  }

+++ src/graphql.ts
  objectType({
    name: 'User',
    definition(t) {
-     t.id('id)
-     t.string('name')
+     t.model.id()
+     t.model.name()
    },
  })

+++ src/graphql.ts
  queryType({
    definition(t) {
      t.list.field('users', {
        type: 'User',
-       resolve() {
-         return [{ id: '1643', name: 'newton' }]
+       resolve(_root, _args, ctx) {
+         return ctx.db.users.findMany()
        },
      })
    },
  })

// tests/__helpers.ts
import { createTestContext, TestContext } from 'nexus/testing'

export function createTestContext(): TestContext {
  let ctx: TestContext

  beforeAll(async () => {
    Object.assign(ctx, await createTestContext())
    await ctx.app.start()
  })

  afterAll(async () => {
    await ctx.app.stop()
  })

  return ctx
}

// tests/foo.spec.ts
